public class Calculadora {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	}

	public Double soma(double d, double e) {
		return d + e;
	}

	public Double subtracao(double d, double e) {
		return d - e;
	}

	public Double multiplicacao(double d, double e) {
		return d * e;
	}

	public Double divisao(double d, double e) {
		return d / e;
	}

	public Double potencia(double d, double e) {
		return Math.pow(d, e);
	}
}
